            ELSE row_to_json(ROW((((listing_changes_mview.hash_ann_parent_symbol)::text || ' '::text) || (listing_changes_mview.hash_ann_symbol)::text), public.strip_tags(listing_changes_mview.hash_full_note_es))::public.api_annotation)
