CREATE OR REPLACE VIEW public.api_taxon_concepts_view AS
 SELECT tc.id,
    tc.parent_id,
    taxonomies.name,
        CASE
            WHEN ((taxonomies.name)::text = 'CITES_EU'::text) THEN true
            ELSE false
        END AS taxonomy_is_cites_eu,
    tc.full_name,
    tc.author_year,
