    (tc.data OPERATOR(public.->) 'family_name'::text) AS family_name,
    (tc.data OPERATOR(public.->) 'genus_name'::text) AS genus_name,
    (tc.data OPERATOR(public.->) 'kingdom_id'::text) AS kingdom_id,
