  WHERE ((tc.name_status)::text = 'S'::text)
  GROUP BY tc.id, tc.parent_id, taxonomies.name, tc.full_name, tc.author_year, ranks.name, tc.taxonomic_position, tc.created_at, tc.dependents_updated_at
